mapper.select:
SELECT COUNT(*) AS CNT
FROM BOARD
WHERE MENU_ID = #{menu_id}

SELECT COUNT(*) AS CNT
FROM BOARD
WHERE MENU_ID = #{menu_id} 
      AND  WRITER = #{writer}

SELECT IDX, 
       MENU_ID, 
       TITLE,
       WRITER,
       TO_CHAR(REGDATE, 'YYYY-MM-DD') REGDATE ,
       HIT 
FROM   BOARD


       AND MENU_ID = #{arg0} 


       AND TITLE = #{arg1} 


       AND WRITER = #{arg2} 


       AND CONTENT = #{arg3} 


 ORDER BY IDX DESC
 OFFSET #{arg4} ROWS FETCH NEXT #{arg5} ROWS ONLY

mapper.update:
UPDATE BOARD
SET HIT = HIT +1
WHERE IDX = #{idx}

UPDATE BOARD
SET TITLE = #{title} ,CONTENT=#{content}
WHERE   IDX = #{idx}
